// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

// Collateralized Loan Contract
contract CollateralizedLoan {
    using SafeMath for uint256;
    // Define the structure of a loan
    struct Loan {
        uint id;
        address payable borrower;
        // Hint: Add a field for the lender's address
        address payable lender;
        uint collateral;
        // Hint: Add fields for loan amount, interest rate, due date, isFunded, isRepaid
        uint loanAmount;
        uint interestRate;
        uint startDate;
        uint dueDate;
        bool isFunded;
        bool isRepaid;
    }

    // Create a mapping to manage the loans
    mapping(uint => Loan) public loans;
    uint public nextLoanId;
    uint private secondsPerYear = 31557600;

    // Hint: Define events for loan requested, funded, repaid, and collateral claimed
    event LoanRequested(uint loanId, uint timestamp);
    event LoanFunded(uint loanId);
    event LoanRepaid(uint loanId);
    event CollateralClaimed(uint loanId);

    // Custom Modifiers
    // Hint: Write a modifier to check if a loan exists
    // Hint: Write a modifier to ensure a loan is not already funded
    modifier loanExists(uint _loanId) {
        require(loans[_loanId].id > 0, "Loan does not exist.");
        _;
    }
    modifier loanIsNotFunded(uint _loanId) {
        require(loans[_loanId].isFunded == false, "Loan is funded.");
        _;
    }
    modifier loanIsFunded(uint _loanId) {
        require(loans[_loanId].isFunded == true, "Loan is not funded.");
        _;
    }
    modifier loanIsNotRepaid(uint _loanId) {
        require(loans[_loanId].isRepaid == false, "Loan is repaid.");
        _;
    }

    // Function to deposit collateral and request a loan
    function depositCollateralAndRequestLoan(uint _loanAmount, uint _interestRate, uint _duration) external payable {
        uint collateralAmount = msg.value;
        // Hint: Check if the collateral is more than 0
        require(collateralAmount > 0, "Collateral must be greater than 0.");
        // Hint: Calculate the loan amount based on the collateralized amount
        uint amount = _loanAmount - collateralAmount;
        uint dateDue = block.timestamp + _duration.div(secondsPerYear);
        uint loanId = nextLoanId;
        loans[loanId] = Loan(
            loanId,
            payable(msg.sender),
            payable(0),
            collateralAmount,
            amount,
            _interestRate,
            block.timestamp,
            dateDue,
            true,
            false
        );

        // Hint: Increment nextLoanId and create a new loan in the loans mapping
        nextLoanId += 1;
        // Hint: Emit an event for loan request
        emit LoanRequested(loanId, block.timestamp);
    }

    // // Function to fund a loan
    // // Hint: Write the fundLoan function with necessary checks and logic
    // function fundLoan(uint _loanId) payable external loanExists(_loanId) loanIsNotFunded(_loanId) {
    //     require(msg.value > 0, "Loan must be greater than 0.");
    //     loans[_loanId].borrower.transfer(msg.value);
    //     loans[_loanId].lender = payable(msg.sender);
    //     loans[_loanId].isFunded = true;

    //     emit LoanFunded(_loanId);
    // }
    // // Function to repay a loan
    // // Hint: Write the repayLoan function with necessary checks and logic
    // function repayLoan(uint _loanId) payable external loanExists(_loanId) {
    //     require(block.timestamp <= loans[_loanId].dueDate, "Loan has expired.");
    //     // Borrowers repay the loan with interest before the due date.
    //     uint interest = (loans[_loanId].interestRate / 100);
    //     uint yearsElapsed = ((block.timestamp - loans[_loanId].startDate) / secondsPerYear);
    //     uint amountDue = interest * loans[_loanId].loanAmount * yearsElapsed;
    //     // Upon successful repayment, the contract returns the collateral to the borrower.
    //     loans[_loanId].lender.transfer(amountDue);
    //     loans[_loanId].borrower.transfer(loans[_loanId].collateral);
    //     loans[_loanId].collateral = 0;
    //     loans[_loanId].isRepaid = true;

    //     emit LoanRepaid(_loanId);
    // }
    // // Function to claim collateral on default
    // // Hint: Write the claimCollateral function with necessary checks and logic
    // function claimCollateral(uint _loanId) external loanExists(_loanId) loanIsFunded(_loanId) loanIsNotRepaid(_loanId) {
    //     loans[_loanId].lender.transfer(loans[_loanId].collateral);
    //     loans[_loanId].collateral = 0;

    //     emit CollateralClaimed(_loanId);
    // }

    // function getLoan(uint _loanId) public view returns (Loan memory) {
    //     return loans[_loanId];
    // }
}

